mapper.insert:
INSERT INTO order_items (
    order_id, product_id, price, quantity, create_time, update_time
) VALUES (
             #{orderId}, #{productId}, #{price}, #{quantity}, NOW(), NOW()
         )

mapper.select:
SELECT o.id AS orderId, o.order_no AS orderNo, i.product_id AS productId, p.title AS productTitle, -- 简单做法：从 images 字段中取第一个 URL，当缩略图 SUBSTRING_INDEX(p.images, ',', 1) AS productThumbnail, i.price AS dealPrice, i.quantity AS quantity, u.nickname AS buyerNickname, o.status AS status, o.shipping_company AS shippingCompany, o.tracking_no AS trackingNo, o.create_time AS createTime, o.pay_time AS payTime FROM orders o JOIN order_items i ON i.order_id = o.id JOIN products p ON p.id = i.product_id JOIN users u ON u.id = o.buyer_id WHERE o.seller_id = #{currentUserId} /* 可选：根据 status 查询待发货、已发货等 */
AND o.status = #{query.status}
ORDER BY o.create_time DESC
