mapper.update:
UPDATE products
SET
    reason = #{product.reason},
    status = #{product.status},
    create_time = #{product.createTime}
WHERE id = #{product.id}

mapper.select:
SELECT id, title, category, status, create_time AS createTime, reason
FROM products
WHERE id = #{productId}

SELECT id, title, category, status, create_time AS createTime
FROM products
WHERE id = #{productId}
