mapper.update:
UPDATE products
SET is_deleted = 1,
    update_time = NOW()
WHERE id = #{id}
  AND owner_id = #{ownerId}
  AND is_deleted = 0

mapper.select:

SELECT

FROM products

    AND is_deleted = 0

        AND title LIKE CONCAT('%', #{productName}, '%')


        AND category = #{category}




            AND status = #{PS_UNDER_REVIEW}





            AND status = #{status}




ORDER BY create_time DESC

SELECT

FROM products
WHERE owner_id = #{userId}
AND is_deleted = 0

    AND status = #{status}

ORDER BY create_time DESC


SELECT 
FROM products p
WHERE p.id = #{productId}
AND p.status = #{PS_ON_SALE}
AND p.is_deleted = 0
LIMIT 1
