mapper.select:
SELECT * FROM user WHERE 1=1
AND (username LIKE CONCAT('%', #{keyword}, '%') OR mobile LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND create_time >= #{startTime}
AND update_time <= #{endTime}
ORDER BY create_time DESC

mapper.update:
UPDATE user
SET status = #{status},
    update_time = #{updateTime}
WHERE id = #{userId}
