mapper.select:
SELECT * FROM users WHERE 1=1
AND (username LIKE CONCAT('%', #{keyword}, '%') OR mobile LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND create_time >= #{startTime}
AND update_time <= #{endTime}
ORDER BY create_time DESC

mapper.update:
UPDATE users
SET status = #{status},
    update_time = #{updateTime}
WHERE id = #{userId}

UPDATE users

    nickname = #{nickname},
    avatar = #{avatar},
    bio = #{bio},
    update_time = #{updateTime}

WHERE id = #{id}

UPDATE users
SET password = #{password},
    update_time = #{updateTime}
WHERE id = #{userId}

UPDATE users
SET mobile = #{mobile},
    update_time = #{updateTime}
WHERE id = #{userId}

UPDATE users
SET email = #{email},
    update_time = #{updateTime}
WHERE id = #{userId}

UPDATE users
SET credit_score = #{creditScore},
    credit_level = #{creditLevel},
    credit_updated_at = #{creditUpdatedAt},
    update_time = #{creditUpdatedAt}
WHERE id = #{userId}
